CREATE TABLE `crop_search` (
  `id` int(11) NOT NULL,
  `crop` varchar(255) NOT NULL,
  `location` varchar(255) NOT NULL,
  `details` text NOT NULL,
  `audio` varchar(255) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

--
-- Dumping data for table `crop_search`
